mapper.update:
update user_private_message


        UNAME2 = #{userNameFrom}


        REPEATSTATUS = #{status}


where UID = #{uid} and UID2 = #{uidFrom} and REPEATTIME = #{repeatTimeFrom}

mapper.delete:
delete from user_private_message


       and UID = #{uid}


       and UID2 = #{uidFrom}


        and UNAME2 = #{userNameFrom}


        and REPEATTIME = #{repeatTimeFrom}


        and REPEATCONTEXT = #{repeatContextFrom}


        and REPEATSTATUS = #{status}

mapper.select:
select UID, UID2, UNAME2, REPEATTIME, REPEATCONTEXT, REPEATSTATUS
FROM user_private_message


        and UID = #{uid}


        and UID2 = #{uidFrom}


        and UNAME2 = #{userNameFrom}


        and REPEATTIME = #{repeatTimeFrom}


        and REPEATCONTEXT = #{repeatContextFrom}


        and REPEATSTATUS = #{status}
